mapper.insert:
insert into t_bc_spec (bc_spec_id, bc_id, spec_id, 
  display_order, status, is_filter
  )
values (#{bcSpecId,jdbcType=INTEGER}, #{bcId,jdbcType=SMALLINT}, #{specId,jdbcType=INTEGER}, 
  #{displayOrder,jdbcType=TINYINT}, #{status,jdbcType=CHAR}, #{isFilter,jdbcType=CHAR}
  )

mapper.update:
    update T_BC_SPEC
    set DISPLAY_ORDER = DISPLAY_ORDER + #{value}
    where BC_ID = #{bcId,jdbcType=DECIMAL} and 
    
		
			DISPLAY_ORDER < #{order} 
		
		
			DISPLAY_ORDER > #{order} 
		
	
	 and STATUS=1

mapper.select:
SELECT bcs.BC_SPEC_ID, bcs.BC_ID, bcs.SPEC_ID, bcs.DISPLAY_ORDER, bcs.STATUS, bcs.IS_FILTER, s.SPEC_NAME FROM T_BC_SPEC bcs, T_SPEC s
bcs.BC_ID = #{bcId,jdbcType=DECIMAL}
and bcs.STATUS = #{status,jdbcType=CHAR}
and bcs.SPEC_ID = s.SPEC_ID
ORDER BY bcs.DISPLAY_ORDER ASC
