mapper.select:
select , concat(min(s.market_price),' ~ ',max(s.market_price)) as market_price,
	concat (min(s.sale_price),' ~ ',max(s.sale_price)) as sale_price
from t_product p ,t_sku s

	 p.prod_id = s.prod_id 
	
		and p.SELLER_ID = #{productCondition.sellerId}
	
	
		and  LOWER(p.title) like LOWER('%'||#{productCondition.title}||'%')
	
	
		and p.CREATE_DATE >= #{productCondition.startDate,jdbcType=TIMESTAMP}
	
	
		and p.CREATE_DATE <= #{productCondition.endDate,jdbcType=TIMESTAMP}
	
	
		and p.STATUS = #{productCondition.status}
	
	
		and p.AUDIT_STATUS = #{productCondition.auditStatus}
	
	group by 
	order by
	p.CREATE_DATE desc

select
from t_product where prod_id in
#{item}
